from django.contrib import admin
from .models import UserAccount, InvitationToken, ReferralSettings
from django.utils import timezone

@admin.register(UserAccount)
class UserAccountAdmin(admin.ModelAdmin):
    list_display =  ('email', 'first_name', 'last_name', 'is_staff', 'is_superuser', 'total_invitations', 'active_invitations', 'invited_by')
    search_fields = ('email', 'first_name', 'last_name', 'invited_by__email')
    list_filter = ('is_staff', 'is_superuser', 'is_active')
    def total_invitations(self, obj):
        return obj.generated_tokens.count()

    def active_invitations(self, obj):
        return obj.generated_tokens.filter(is_used=False, expires_at__gt=timezone.now()).count()
    def inviter_email(self, obj):
        if obj.invited_by:
            return obj.invited_by.email
        return '-'

    inviter_email.short_description = "Invited By Email"
    total_invitations.short_description = "Total Invitations"
    active_invitations.short_description = "Active Invitations"
    # This helps admins quickly see who has sent how many invites.
@admin.register(InvitationToken)
class InvitationTokenAdmin(admin.ModelAdmin):
    list_display = ('token', 'inviter', 'is_used', 'created_at')
    list_filter = ('is_used',)

# This gives admins a UI to control referral behavior.
@admin.register(ReferralSettings)
class ReferralSettingsAdmin(admin.ModelAdmin):
    list_display = ('max_tokens_per_user', 'allow_unlimited_tokens', 'token_expiration_days')
    fields = ['max_tokens_per_user', 'allow_unlimited_tokens', 'token_expiration_days']

